     1→/**
     2→ * Chess Study Tool - Background Service Worker
     3→ * 
     4→ * Standalone learning tool:
     5→ * - Captures screenshots on user request only
     6→ * - Analyzes with Claude Vision
     7→ * - Gets Stockfish moves from Chess-API.com
     8→ * - Returns results to panel
     9→ * 
    10→ * NO INTERACTION with any chess website
    11→ */
    12→
    13→// ============================================================================
    14→// CONFIGURATION
    15→// ============================================================================
    16→
    17→const CONFIG = {
    18→  CHESS_API_URL: 'https://chess-api.com/v1',
    19→  CLAUDE_API_URL: 'https://api.anthropic.com/v1/messages',
    20→  OPENROUTER_API_URL: 'https://openrouter.ai/api/v1/chat/completions',
    21→  CLAUDE_MODEL: 'claude-sonnet-4-5-20250929'
    22→};
    23→
    24→// ============================================================================
    25→// EXTENSION ICON CLICK - Open Side Panel
    26→// ============================================================================
    27→
    28→chrome.action.onClicked.addListener(async (tab) => {
    29→  // Open the side panel
    30→  await chrome.sidePanel.open({ windowId: tab.windowId });
    31→});
    32→
    33→// ============================================================================
    34→// MESSAGE HANDLING
    35→// ============================================================================
    36→
    37→chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
    38→  console.log('[Chess Study] Message received:', message.type);
    39→  
    40→  if (message.type === 'CAPTURE_SCREENSHOT') {
    41→    handleCapture(sendResponse);
    42→    return true; // Keep channel open for async
    43→  }
    44→  
    45→  if (message.type === 'ANALYZE_SCREENSHOT') {
    46→    console.log('[Chess Study] Starting analysis...');
    47→    handleAnalysis(message.imageData)
    48→      .then(result => {
    49→        console.log('[Chess Study] Analysis complete:', result);
    50→        sendResponse(result);
    51→      })
    52→      .catch(error => {
    53→        console.error('[Chess Study] Analysis failed:', error);
    54→        sendResponse({ error: error.message });
    55→      });
    56→    return true;
    57→  }
    58→  
    59→  if (message.type === 'TEST_ANTHROPIC_API') {
    60→    testAnthropicAPI(message.apiKey, message.provider || 'anthropic')
    61→      .then(result => sendResponse(result))
    62→      .catch(error => sendResponse({ success: false, error: error.message }));
    63→    return true;
    64→  }
    65→  
    66→  if (message.type === 'TEST_STOCKFISH_API') {
    67→    testStockfishAPI()
    68→      .then(result => sendResponse(result))
    69→      .catch(error => sendResponse({ success: false, error: error.message }));
    70→    return true;
    71→  }
    72→  
    73→  return false;
    74→});
    75→
    76→// ============================================================================
    77→// SCREENSHOT CAPTURE
    78→// ============================================================================
    79→
    80→async function handleCapture(sendResponse) {
    81→  try {
    82→    // Get the current active tab in the current window
    83→    const [activeTab] = await chrome.tabs.query({ active: true, currentWindow: true });
    84→    
    85→    if (!activeTab) {
    86→      sendResponse({ success: false, error: 'No active tab found. Please open a tab with a chess position.' });
    87→      return;
    88→    }
    89→    
    90→    // Capture the visible area of the active tab's window
    91→    const imageData = await chrome.tabs.captureVisibleTab(activeTab.windowId, {
    92→      format: 'png',
    93→      quality: 100
    94→    });
    95→    
    96→    console.log('[Chess Study] Screenshot captured from tab', activeTab.id);
    97→    sendResponse({ success: true, imageData });
    98→    
    99→  } catch (error) {
   100→    console.error('[Chess Study] Capture error:', error);
   101→    sendResponse({ success: false, error: error.message });
   102→  }
   103→}
   104→
   105→// ============================================================================
   106→// ANALYSIS PIPELINE
   107→// ============================================================================
   108→
   109→async function handleAnalysis(imageData) {
   110→  try {
   111→    // Get settings
   112→    const settings = await chrome.storage.sync.get({
   113→      claudeApiKey: '',
   114→      apiProvider: 'anthropic',
   115→      apiModel: 'claude-sonnet-4-5-20250929',
   116→      numMoves: 5,
   117→      depth: 18
   118→    });
   119→    
   120→    if (!settings.claudeApiKey) {
   121→      return { error: 'API key not configured' };
   122→    }
   123→    
   124→    // Step 1: Claude Vision - recognize the position
   125→    console.log('[Chess Study] Step 1: Analyzing with Vision...');
   126→    let visionResult;
   127→    try {
   128→      visionResult = await analyzeWithVision(imageData, settings.claudeApiKey, settings.apiProvider, settings.apiModel);
   129→      console.log('[Chess Study] Vision result:', visionResult);
   130→    } catch (visionError) {
   131→      console.error('[Chess Study] Vision error:', visionError);
   132→      return { error: 'Vision analysis failed: ' + visionError.message };
   133→    }
   134→    
   135→    if (!visionResult.fen) {
   136→      return { 
   137→        error: 'Could not recognize a chess position in the screenshot. Make sure a chess board is visible.',
   138→        ...visionResult
   139→      };
   140→    }
   141→    
   142→    // Step 2: Stockfish - get best moves
   143→    console.log('[Chess Study] Step 2: Getting Stockfish analysis...');
   144→    console.log('[Chess Study] FEN to analyze:', visionResult.fen);
   145→    let moves;
   146→    try {
   147→      moves = await getStockfishMoves(visionResult.fen, settings.depth, settings.numMoves);
   148→      console.log('[Chess Study] Stockfish moves:', moves);
   149→    } catch (stockfishError) {
   150→      console.error('[Chess Study] Stockfish error:', stockfishError);
   151→      return { 
   152→        error: `Stockfish analysis failed: ${stockfishError.message}\n\nFEN was: ${visionResult.fen}`,
   153→        fen: visionResult.fen,
   154→        turn: visionResult.turn
   155→      };
   156→    }
   157→    
   158→    // Step 3: Claude - get explanation
   159→    console.log('[Chess Study] Step 3: Getting explanation...');
   160→    let explanation;
   161→    try {
   162→      explanation = await getExplanation(visionResult.fen, moves, settings.claudeApiKey, settings.apiProvider, settings.apiModel);
   163→      console.log('[Chess Study] Explanation:', explanation);
   164→    } catch (explainError) {
   165→      console.error('[Chess Study] Explanation error:', explainError);
   166→      explanation = 'Could not generate explanation.';
   167→    }
   168→    
   169→    const result = {
   170→      fen: visionResult.fen,
   171→      turn: visionResult.turn,
   172→      description: visionResult.description,
   173→      moves,
   174→      explanation
   175→    };
   176→    
   177→    console.log('[Chess Study] Final result:', result);
   178→    return result;
   179→    
   180→  } catch (error) {
   181→    console.error('[Chess Study] Analysis error:', error);
   182→    return { error: error.message };
   183→  }
   184→}
   185→
   186→// ============================================================================
   187→// CLAUDE VISION
   188→// ============================================================================
   189→
   190→async function analyzeWithVision(imageDataUrl, apiKey, provider = 'anthropic', model = 'claude-sonnet-4-5-20250929') {
   191→  const base64Data = imageDataUrl.replace(/^data:image\/\w+;base64,/, '');
   192→  
   193→  const prompt = `You are a chess position analyzer. Look at this screenshot and find any chess board visible.
   194→
   195→Your task:
   196→1. Locate the chess board in the image
   197→2. Carefully identify every piece and its exact square
   198→3. Determine whose turn it is (look for visual cues like clocks, highlights, or turn indicators)
   199→4. Convert the position to FEN notation
   200→
   201→PIECE IDENTIFICATION:
   202→- White pieces: K (King), Q (Queen), R (Rook), B (Bishop), N (Knight), P (Pawn) - usually lighter colored
   203→- Black pieces: k, q, r, b, n, p - usually darker colored
   204→
   205→BOARD ORIENTATION:
   206→- Standard view: White pieces start on ranks 1-2, Black on ranks 7-8
   207→- If viewing from Black's side, mentally flip the board
   208→- The a1 square is always dark (from White's perspective, bottom-left)
   209→
   210→CRITICAL RULES FOR FEN:
   211→1. Each side starts with EXACTLY 8 pawns - if a pawn has moved, it's NO LONGER on its starting square
   212→2. When a pawn moves from e2 to e4, rank 2 shows "PPPP1PPP" (missing the e-pawn), NOT "PPPPPPPP"
   213→3. Count pieces carefully: White has max 8 pawns, Black has max 8 pawns
   214→4. Pawns CANNOT be on rank 1 or rank 8 (they would promote)
   215→5. Each rank in FEN must sum to exactly 8 squares
   216→
   217→FEN FORMAT (exactly 6 space-separated parts):
   218→1. Piece placement (8 ranks separated by /)
   219→2. Active color: "w" or "b"
   220→3. Castling: "KQkq", "Kq", "-", etc.
   221→4. En passant: square like "e3" or "-"
   222→5. Halfmove clock: number (use "0")
   223→6. Fullmove: number (use "1")
   224→
   225→VALIDATION CHECKLIST before responding:
   226→□ Exactly 8 ranks separated by /
   227→□ Each rank sums to 8 (pieces + numbers)
   228→□ White pawns ≤ 8, Black pawns ≤ 8
   229→□ No pawns on ranks 1 or 8
   230→□ Exactly 1 white King, 1 black King
   231→□ Moved pieces are NOT on their original squares
   232→
   233→OUTPUT FORMAT (JSON only):
   234→{
   235→  "fen": "rnbqkbnr/ppppp1pp/8/5p2/3P4/8/PPP1PPPP/RNBQKBNR w KQkq f6 0 2",
   236→  "turn": "w",
   237→  "description": "Dutch Defense after 1.d4 f5",
   238→  "confidence": "high"
   239→}
   240→
   241→If NO chess board is found:
   242→{
   243→  "fen": null,
   244→  "error": "No chess board detected"
   245→}`;
   246→
   247→  let response;
   248→  let data;
   249→  
   250→  if (provider === 'openrouter') {
   251→    // OpenRouter API
   252→    // Map Anthropic model IDs to OpenRouter format
   253→    let openRouterModel = model;
   254→    if (model.includes('claude-sonnet-4-5')) {
   255→      openRouterModel = 'anthropic/claude-sonnet-4.5';
   256→    } else if (model.includes('claude-haiku-4-5')) {
   257→      openRouterModel = 'anthropic/claude-haiku-4.5';
   258→    } else if (model.startsWith('claude-')) {
   259→      openRouterModel = `anthropic/${model}`;
   260→    }
   261→    
   262→    response = await fetch(CONFIG.OPENROUTER_API_URL, {
   263→      method: 'POST',
   264→      headers: {
   265→        'Content-Type': 'application/json',
   266→        'Authorization': `Bearer ${apiKey}`,
   267→        'HTTP-Referer': 'chrome-extension://chess-study-tool',
   268→        'X-Title': 'Chess Study Tool'
   269→      },
   270→      body: JSON.stringify({
   271→        model: openRouterModel,
   272→        max_tokens: 1000,
   273→        messages: [{
   274→          role: 'user',
   275→          content: [
   276→            {
   277→              type: 'image_url',
   278→              image_url: {
   279→                url: `data:image/png;base64,${base64Data}`
   280→              }
   281→            },
   282→            { type: 'text', text: prompt }
   283→          ]
   284→        }]
   285→      })
   286→    });
   287→
   288→    if (!response.ok) {
   289→      const err = await response.json().catch(() => ({}));
   290→      throw new Error(err.error?.message || `OpenRouter API error: ${response.status}`);
   291→    }
   292→
   293→    data = await response.json();
   294→    const text = data.choices?.[0]?.message?.content || '';
   295→    
   296→    // Parse JSON response
   297→    try {
   298→      const jsonMatch = text.match(/\{[\s\S]*\}/);
   299→      if (jsonMatch) {
   300→        const result = JSON.parse(jsonMatch[0]);
   301→        return {
   302→          fen: result.fen,
   303→          turn: result.turn || (result.fen?.split(' ')[1]) || 'w',
   304→          description: result.description,
   305→          confidence: result.confidence
   306→        };
   307→      }
   308→    } catch (e) {
   309→      console.error('[Chess Study] Vision parse error:', e);
   310→    }
   311→    
   312→  } else {
   313→    // Anthropic API (direct)
   314→    response = await fetch(CONFIG.CLAUDE_API_URL, {
   315→      method: 'POST',
   316→      headers: {
   317→        'Content-Type': 'application/json',
   318→        'x-api-key': apiKey,
   319→        'anthropic-version': '2023-06-01',
   320→        'anthropic-dangerous-direct-browser-access': 'true'
   321→      },
   322→      body: JSON.stringify({
   323→        model: model,
   324→        max_tokens: 1000,
   325→        messages: [{
   326→          role: 'user',
   327→          content: [
   328→            {
   329→              type: 'image',
   330→              source: {
   331→                type: 'base64',
   332→                media_type: 'image/png',
   333→                data: base64Data
   334→              }
   335→            },
   336→            { type: 'text', text: prompt }
   337→          ]
   338→        }]
   339→      })
   340→    });
   341→
   342→    if (!response.ok) {
   343→      const err = await response.json().catch(() => ({}));
   344→      throw new Error(err.error?.message || `Claude API error: ${response.status}`);
   345→    }
   346→
   347→    data = await response.json();
   348→    const text = data.content?.[0]?.text || '';
   349→    console.log('[Chess Study] Vision raw response:', text);
   350→    
   351→    // Parse JSON response
   352→    try {
   353→      const jsonMatch = text.match(/\{[\s\S]*\}/);
   354→      if (jsonMatch) {
   355→        const result = JSON.parse(jsonMatch[0]);
   356→        console.log('[Chess Study] Vision parsed FEN:', result.fen);
   357→        return {
   358→          fen: result.fen,
   359→          turn: result.turn || (result.fen?.split(' ')[1]) || 'w',
   360→          description: result.description,
   361→          confidence: result.confidence
   362→        };
   363→      }
   364→    } catch (e) {
   365→      console.error('[Chess Study] Vision parse error:', e);
   366→    }
   367→  }
   368→  
   369→  return { fen: null };
   370→}
   371→
   372→// ============================================================================
   373→// STOCKFISH (Chess-API.com)
   374→// ============================================================================
   375→
   376→// Validate FEN string format
   377→function validateFEN(fen) {
   378→  if (!fen || typeof fen !== 'string') {
   379→    return { valid: false, error: 'FEN is empty or not a string' };
   380→  }
   381→  
   382→  const parts = fen.trim().split(' ');
   383→  
   384→  // Must have at least position and turn
   385→  if (parts.length < 2) {
   386→    return { valid: false, error: `FEN should have at least 2 parts, got ${parts.length}` };
   387→  }
   388→  
   389→  // Validate board position (first part)
   390→  const board = parts[0];
   391→  const ranks = board.split('/');
   392→  
   393→  if (ranks.length !== 8) {
   394→    return { valid: false, error: `Board should have 8 ranks, got ${ranks.length}` };
   395→  }
   396→  
   397→  // Count pieces
   398→  let whiteKings = 0, blackKings = 0;
   399→  let whitePawns = 0, blackPawns = 0;
   400→  
   401→  // Validate each rank
   402→  for (let i = 0; i < 8; i++) {
   403→    const rank = ranks[i];
   404→    const rankNum = 8 - i; // Rank 8 is index 0, rank 1 is index 7
   405→    let squares = 0;
   406→    
   407→    for (const char of rank) {
   408→      if ('12345678'.includes(char)) {
   409→        squares += parseInt(char);
   410→      } else if ('pnbrqkPNBRQK'.includes(char)) {
   411→        squares += 1;
   412→        if (char === 'K') whiteKings++;
   413→        if (char === 'k') blackKings++;
   414→        if (char === 'P') {
   415→          whitePawns++;
   416→          // Pawns can't be on rank 1 or 8
   417→          if (rankNum === 1 || rankNum === 8) {
   418→            return { valid: false, error: `White pawn on rank ${rankNum} is illegal` };
   419→          }
   420→        }
   421→        if (char === 'p') {
   422→          blackPawns++;
   423→          // Pawns can't be on rank 1 or 8
   424→          if (rankNum === 1 || rankNum === 8) {
   425→            return { valid: false, error: `Black pawn on rank ${rankNum} is illegal` };
   426→          }
   427→        }
   428→      } else {
   429→        return { valid: false, error: `Invalid character '${char}' in rank ${rankNum}` };
   430→      }
   431→    }
   432→    
   433→    if (squares !== 8) {
   434→      return { valid: false, error: `Rank ${rankNum} has ${squares} squares, should have 8` };
   435→    }
   436→  }
   437→  
   438→  // Must have exactly one king of each color
   439→  if (whiteKings !== 1) {
   440→    return { valid: false, error: `Must have exactly 1 white King, found ${whiteKings}` };
   441→  }
   442→  if (blackKings !== 1) {
   443→    return { valid: false, error: `Must have exactly 1 black King, found ${blackKings}` };
   444→  }
   445→  
   446→  // Maximum 8 pawns per side
   447→  if (whitePawns > 8) {
   448→    return { valid: false, error: `White has ${whitePawns} pawns, maximum is 8` };
   449→  }
   450→  if (blackPawns > 8) {
   451→    return { valid: false, error: `Black has ${blackPawns} pawns, maximum is 8` };
   452→  }
   453→  
   454→  // Validate turn
   455→  if (parts[1] && !['w', 'b'].includes(parts[1])) {
   456→    return { valid: false, error: `Invalid turn '${parts[1]}', should be 'w' or 'b'` };
   457→  }
   458→  
   459→  return { valid: true };
   460→}
   461→
   462→// Ensure FEN has all 6 fields with valid values
   463→function normalizeFEN(fen) {
   464→  const parts = fen.trim().split(' ');
   465→  
   466→  // Default values for missing or invalid parts
   467→  const position = parts[0];
   468→  const turn = ['w', 'b'].includes(parts[1]) ? parts[1] : 'w';
   469→  
   470→  // Castling - validate or default to '-'
   471→  let castling = '-';
   472→  if (parts[2] && parts[2] !== '-') {
   473→    const validCastling = parts[2].split('').filter(c => 'KQkq'.includes(c)).join('');
   474→    castling = validCastling || '-';
   475→  }
   476→  
   477→  // En passant - validate or default to '-'
   478→  let enPassant = '-';
   479→  if (parts[3] && /^[a-h][36]$/.test(parts[3])) {
   480→    enPassant = parts[3];
   481→  }
   482→  
   483→  // Move counters - ensure they're valid numbers
   484→  const halfmove = /^\d+$/.test(parts[4]) ? parts[4] : '0';
   485→  const fullmove = /^\d+$/.test(parts[5]) ? parts[5] : '1';
   486→  
   487→  return `${position} ${turn} ${castling} ${enPassant} ${halfmove} ${fullmove}`;
   488→}
   489→
   490→async function getStockfishMoves(fen, depth, numMoves) {
   491→  console.log('[Chess Study] Raw FEN from Vision:', fen);
   492→  
   493→  // Validate FEN
   494→  const validation = validateFEN(fen);
   495→  if (!validation.valid) {
   496→    console.error('[Chess Study] FEN validation failed:', validation.error);
   497→    throw new Error(`Invalid FEN: ${validation.error}`);
   498→  }
   499→  
   500→  // Normalize FEN to ensure all 6 fields with valid values
   501→  const normalizedFEN = normalizeFEN(fen);
   502→  console.log('[Chess Study] Normalized FEN:', normalizedFEN);
   503→  
   504→  const requestBody = {
   505→    fen: normalizedFEN,
   506→    depth: Math.min(depth, 18),
   507→    variants: Math.min(numMoves, 5),
   508→    maxThinkingTime: 100
   509→  };
   510→  console.log('[Chess Study] Request body:', JSON.stringify(requestBody));
   511→  
   512→  const response = await fetch(CONFIG.CHESS_API_URL, {
   513→    method: 'POST',
   514→    headers: { 'Content-Type': 'application/json' },
   515→    body: JSON.stringify(requestBody)
   516→  });
   517→
   518→  if (!response.ok) {
   519→    const errorText = await response.text();
   520→    console.error('[Chess Study] Stockfish API error:', response.status, errorText);
   521→    throw new Error(`Stockfish API error: ${response.status}`);
   522→  }
   523→
   524→  const data = await response.json();
   525→  console.log('[Chess Study] Raw Stockfish response:', JSON.stringify(data));
   526→  
   527→  // Check for error response from chess-api.com
   528→  if (data.type && data.type.includes('ERROR')) {
   529→    console.error('[Chess Study] Chess API returned error:', data.type, data.text);
   530→    console.error('[Chess Study] FEN that caused error:', normalizedFEN);
   531→    throw new Error(`${data.text || data.type}\nFEN: ${normalizedFEN}`);
   532→  }
   533→  
   534→  // Handle various response formats from Chess-API.com
   535→  const moves = [];
   536→  
   537→  // Format 1: Array of moves
   538→  if (Array.isArray(data)) {
   539→    console.log('[Chess Study] Response is array with', data.length, 'items');
   540→    data.forEach(d => moves.push(normalizeMove(d)));
   541→  } 
   542→  // Format 2: Single move object with 'move' or 'lan' property
   543→  else if (data && (data.move || data.lan || data.san)) {
   544→    console.log('[Chess Study] Response is single move object');
   545→    moves.push(normalizeMove(data));
   546→  }
   547→  // Format 3: Object with nested data
   548→  else if (data && data.data) {
   549→    console.log('[Chess Study] Response has nested data property');
   550→    if (Array.isArray(data.data)) {
   551→      data.data.forEach(d => moves.push(normalizeMove(d)));
   552→    } else {
   553→      moves.push(normalizeMove(data.data));
   554→    }
   555→  }
   556→  // Format 4: Object with 'bestmove' property
   557→  else if (data && data.bestmove) {
   558→    console.log('[Chess Study] Response has bestmove property');
   559→    moves.push({
   560→      move: data.bestmove,
   561→      san: data.san || data.bestmove,
   562→      evaluation: data.eval || data.score || 0,
   563→      depth: data.depth,
   564→      continuation: data.pv ? data.pv.split(' ') : [],
   565→      winChance: data.winChance
   566→    });
   567→  }
   568→  // Format 5: Check for error in response
   569→  else if (data && data.error) {
   570→    console.error('[Chess Study] API returned error:', data.error);
   571→    throw new Error('Chess API error: ' + data.error);
   572→  }
   573→  else {
   574→    console.warn('[Chess Study] Unknown response format:', data);
   575→    // Try to extract any useful info
   576→    if (data && typeof data === 'object') {
   577→      const keys = Object.keys(data);
   578→      console.log('[Chess Study] Response keys:', keys);
   579→    }
   580→  }
   581→  
   582→  console.log('[Chess Study] Parsed moves:', moves);
   583→  
   584→  if (moves.length === 0) {
   585→    console.warn('[Chess Study] No moves parsed from response');
   586→  }
   587→  
   588→  return moves;
   589→}
   590→
   591→function normalizeMove(d) {
   592→  console.log('[Chess Study] Normalizing move:', JSON.stringify(d));
   593→  
   594→  // Handle string input (just the move notation)
   595→  if (typeof d === 'string') {
   596→    return {
   597→      move: d,
   598→      san: d,
   599→      from: d.substring(0, 2),
   600→      to: d.substring(2, 4),
   601→      evaluation: 0,
   602→      depth: 0,
   603→      continuation: [],
   604→      winChance: null
   605→    };
   606→  }
   607→  
   608→  // Build the move string from from/to if available
   609→  let moveStr = d.move || d.lan || '';
   610→  let fromSquare = d.from || '';
   611→  let toSquare = d.to || '';
   612→  
   613→  // If we have from/to but no move string, build it
   614→  if (!moveStr && fromSquare && toSquare) {
   615→    moveStr = fromSquare + toSquare;
   616→  }
   617→  
   618→  // If we have move string but no from/to, parse it
   619→  if (moveStr && moveStr.length >= 4 && !fromSquare) {
   620→    fromSquare = moveStr.substring(0, 2);
   621→    toSquare = moveStr.substring(2, 4);
   622→  }
   623→  
   624→  // Handle object input
   625→  return {
   626→    move: moveStr,
   627→    san: d.san || d.move || d.lan || '',
   628→    from: fromSquare,
   629→    to: toSquare,
   630→    evaluation: d.eval ?? d.score ?? d.evaluation ?? (d.centipawns ? d.centipawns / 100 : 0),
   631→    depth: d.depth || 0,
   632→    continuation: d.continuationArr || d.continuation || (d.pv ? d.pv.split(' ') : []),
   633→    winChance: d.winChance ?? d.wdl ?? null
   634→  };
   635→}
