   697→      })
   698→    });
   699→
   700→    if (!response.ok) {
   701→      return 'Could not generate explanation.';
   702→    }
   703→
   704→    const data = await response.json();
   705→    return data.choices?.[0]?.message?.content || 'No explanation available.';
   706→    
   707→  } else {
   708→    response = await fetch(CONFIG.CLAUDE_API_URL, {
   709→      method: 'POST',
   710→      headers: {
   711→        'Content-Type': 'application/json',
   712→        'x-api-key': apiKey,
   713→        'anthropic-version': '2023-06-01',
   714→        'anthropic-dangerous-direct-browser-access': 'true'
   715→      },
   716→      body: JSON.stringify({
   717→        model: model,
   718→        max_tokens: 400,
   719→        messages: [{ role: 'user', content: prompt }]
   720→      })
   721→    });
   722→
   723→    if (!response.ok) {
   724→      return 'Could not generate explanation.';
   725→    }
   726→
   727→    const data = await response.json();
   728→    return data.content?.[0]?.text || 'No explanation available.';
   729→  }
   730→}
   731→
   732→// ============================================================================
   733→// INIT
   734→// ============================================================================
   735→
   736→console.log('[Chess Study Tool] Service worker initialized - standalone learning tool');
   737→
