async function testStockfishAPI() {
  console.log('[Chess Study] Testing Stockfish API...');

  // Test with starting position
  const testFen = 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1';

