   491→// Unicode pieces
   492→const PIECE_ICONS = {
   493→  'K': '♔', 'Q': '♕', 'R': '♖', 'B': '♗', 'N': '♘', 'P': '♙',
   494→  'k': '♚', 'q': '♛', 'r': '♜', 'b': '♝', 'n': '♞', 'p': '♟'
   495→};
   496→
   497→// Get piece at square from FEN
   498→function getPieceAtSquare(fen, square) {
   499→  if (!fen || !square) return null;
   500→  
   501→  const file = square.charCodeAt(0) - 97; // a=0, h=7
   502→  const rank = 8 - parseInt(square[1]);   // 8=0, 1=7
   503→  
   504→  const boardPart = fen.split(' ')[0];
   505→  const ranks = boardPart.split('/');
   506→  
   507→  if (rank < 0 || rank > 7) return null;
   508→  
   509→  const rankStr = ranks[rank];
   510→  let col = 0;
   511→  
   512→  for (const char of rankStr) {
   513→    if ('12345678'.includes(char)) {
   514→      col += parseInt(char);
   515→    } else {
   516→      if (col === file) {
   517→        return char;
   518→      }
   519→      col++;
   520→    }
   521→    if (col > file) break;
   522→  }
   523→  
   524→  return null;
   525→}
