   530→    'K': 'King', 'Q': 'Queen', 'R': 'Rook', 'B': 'Bishop', 'N': 'Knight', 'P': 'Pawn',
   531→    'k': 'King', 'q': 'Queen', 'r': 'Rook', 'b': 'Bishop', 'n': 'Knight', 'p': 'Pawn'
   532→  };
   533→  
   534→  // Create a vertical list of all moves
   535→  let html = '';
   536→  
   537→  moves.forEach((move, i) => {
   538→    const fromSquare = move.from || (move.move ? move.move.substring(0, 2) : '');
   539→    const toSquare = move.to || (move.move ? move.move.substring(2, 4) : '');
   540→    
   541→    // Get the piece that's moving
   542→    const piece = getPieceAtSquare(fen, fromSquare);
   543→    const pieceIcon = piece ? PIECE_ICONS[piece] : '';
   544→    const pieceName = piece ? PIECE_NAMES[piece] : '';
   545→    const isWhitePiece = piece && piece === piece.toUpperCase();
   546→    
   547→    const isBest = i === 0;
   548→    const rankDisplay = isBest ? '👑' : (i + 1);
   549→    
   550→    html += `
   551→      <div class="move-row ${isBest ? 'best' : ''} ${isBest ? 'active' : ''}" data-move-index="${i}">
   552→        <span class="move-rank">${rankDisplay}</span>
   553→        <span class="move-piece-icon ${isWhitePiece ? 'white-piece' : 'black-piece'}">${pieceIcon}</span>
   554→        <div class="move-text">
   555→          <span class="move-piece-name">${pieceName}</span>
   556→          <span class="move-from-square">${fromSquare}</span>
   557→          <span class="move-arrow">→</span>
   558→          <span class="move-to-square">${toSquare}</span>
   559→        </div>
   560→        <span class="move-eval ${getEvalClass(move.evaluation)}">${formatEval(move.evaluation)}</span>
   561→      </div>
   562→    `;
   563→  });
   564→  
   565→  movesList.innerHTML = html;
   566→  
   567→  // Add click handlers to highlight moves on board
   568→  document.querySelectorAll('.move-row').forEach(row => {
   569→    row.addEventListener('click', () => {
   570→      const index = parseInt(row.dataset.moveIndex);
   571→      if (currentMoves && currentMoves[index]) {
   572→        renderChessBoard(currentFen, currentMoves[index]);
   573→        // Update active state - keep best class but toggle active
   574→        document.querySelectorAll('.move-row').forEach(r => r.classList.remove('active'));
   575→        row.classList.add('active');
