   581→// Render a mini board with arrow showing the move
   582→function renderMiniBoard(container, fen, move) {
   583→  if (!fen) return;
   584→  
   585→  const MINI_PIECES = {
   586→    'K': '♔', 'Q': '♕', 'R': '♖', 'B': '♗', 'N': '♘', 'P': '♙',
   587→    'k': '♚', 'q': '♛', 'r': '♜', 'b': '♝', 'n': '♞', 'p': '♟'
   588→  };
   589→  
   590→  // Parse move to get from/to squares
   591→  let fromSquare = null;
   592→  let toSquare = null;
   593→  
   594→  const moveStr = move.move || '';
   595→  if (moveStr.length >= 4 && /^[a-h][1-8][a-h][1-8]/.test(moveStr)) {
   596→    fromSquare = moveStr.substring(0, 2);
   597→    toSquare = moveStr.substring(2, 4);
   598→  }
   599→  
   600→  // Parse FEN
   601→  const boardPart = fen.split(' ')[0];
   602→  const ranks = boardPart.split('/');
   603→  
   604→  // Build mini board HTML
   605→  let boardHtml = '<div class="mini-board">';
   606→  
   607→  for (let rankIndex = 0; rankIndex < 8; rankIndex++) {
   608→    const rank = ranks[rankIndex];
   609→    const rankNum = 8 - rankIndex;
   610→    let fileIndex = 0;
   611→    
   612→    for (const char of rank) {
   613→      if ('12345678'.includes(char)) {
   614→        const emptyCount = parseInt(char);
   615→        for (let i = 0; i < emptyCount; i++) {
   616→          const file = String.fromCharCode(97 + fileIndex);
   617→          const square = file + rankNum;
   618→          const isLight = (rankIndex + fileIndex) % 2 === 0;
   619→          const highlight = getMiniBoardHighlight(square, fromSquare, toSquare);
   620→          
   621→          boardHtml += `<div class="mini-square ${isLight ? 'light' : 'dark'} ${highlight}"></div>`;
   622→          fileIndex++;
   623→        }
   624→      } else {
   625→        const file = String.fromCharCode(97 + fileIndex);
   626→        const square = file + rankNum;
   627→        const isLight = (rankIndex + fileIndex) % 2 === 0;
   628→        const isWhite = char === char.toUpperCase();
   629→        const pieceChar = MINI_PIECES[char] || char;
   630→        const highlight = getMiniBoardHighlight(square, fromSquare, toSquare);
   631→        
   632→        boardHtml += `<div class="mini-square ${isLight ? 'light' : 'dark'} ${highlight}">
   633→          <span class="mini-piece ${isWhite ? 'white' : 'black'}">${pieceChar}</span>
   634→        </div>`;
   635→        fileIndex++;
   636→      }
   637→    }
   638→  }
   639→  
   640→  boardHtml += '</div>';
   641→  
   642→  // Add SVG arrow overlay if we have from/to squares
   643→  if (fromSquare && toSquare) {
   644→    const boardSize = 160;
   645→    const from = squareToCoords(fromSquare, boardSize);
   646→    const to = squareToCoords(toSquare, boardSize);
   647→    
   648→    // Shorten arrow slightly so it doesn't overlap with arrowhead
   649→    const dx = to.x - from.x;
   650→    const dy = to.y - from.y;
   651→    const length = Math.sqrt(dx * dx + dy * dy);
   652→    const shortenBy = 8;
   653→    const toX = to.x - (dx / length) * shortenBy;
   654→    const toY = to.y - (dy / length) * shortenBy;
   655→    
   656→    boardHtml += `
   657→      <svg class="move-arrow-svg" viewBox="0 0 ${boardSize} ${boardSize}">
   658→        <defs>
   659→          <marker id="arrowhead-${fromSquare}${toSquare}" markerWidth="10" markerHeight="7" 
   660→            refX="9" refY="3.5" orient="auto" fill="rgba(0, 150, 0, 0.9)">
   661→            <polygon points="0 0, 10 3.5, 0 7" />
   662→          </marker>
   663→        </defs>
   664→        <line class="move-arrow" 
   665→          x1="${from.x}" y1="${from.y}" 
   666→          x2="${toX}" y2="${toY}"
   667→          marker-end="url(#arrowhead-${fromSquare}${toSquare})" />
   668→      </svg>
   669→    `;
   670→  }
   671→  
   672→  container.innerHTML = boardHtml;
   673→}
   674→
   675→// Helper to get square coordinates for arrow drawing
   676→function squareToCoords(square, boardSize) {
   677→  const file = square.charCodeAt(0) - 97; // a=0, h=7
   678→  const rank = 8 - parseInt(square[1]);   // 1=7, 8=0
   679→  const cellSize = boardSize / 8;
   680→  return {
   681→    x: file * cellSize + cellSize / 2,
   682→    y: rank * cellSize + cellSize / 2
   683→  };
   684→}
   685→
   686→function getMiniBoardHighlight(square, fromSquare, toSquare) {
   687→  if (square === fromSquare) return 'from-square';
   688→  if (square === toSquare) return 'to-square';
   689→  return '';
   690→}
