   692→function displayExplanation(text) {
   693→  // Highlight chess terminology
   694→  const terms = [
   695→    'check', 'checkmate', 'stalemate', 'castling', 'en passant',
   696→    'fork', 'pin', 'skewer', 'discovered attack', 'double attack',
   697→    'zwischenzug', 'zugzwang', 'tempo', 'initiative', 'development',
   698→    'center control', 'king safety', 'pawn structure', 'outpost',
   699→    'fianchetto', 'gambit', 'sacrifice', 'exchange', 'material',
   700→    'positional', 'tactical', 'endgame', 'middlegame', 'opening',
   701→    'threat', 'counterplay', 'prophylaxis', 'weakness', 'pressure'
   702→  ];
   703→  
   704→  let formatted = text;
   705→  
   706→  terms.forEach(term => {
   707→    const regex = new RegExp(`\\b(${term})\\b`, 'gi');
   708→    formatted = formatted.replace(regex, '<span class="chess-term">$1</span>');
   709→  });
   710→  
   711→  // Highlight move notation
   712→  const moveRegex = /\b([KQRBN]?[a-h]?[1-8]?x?[a-h][1-8](?:=[QRBN])?[+#]?|O-O(?:-O)?)\b/g;
   713→  formatted = formatted.replace(moveRegex, '<span class="move-inline">$1</span>');
   714→  
   715→  explanationText.innerHTML = formatted;
   716→}
