   722→// ============================================================================
   723→// CHESS BOARD RENDERING
   724→// ============================================================================
   725→
   726→// Unicode chess pieces
   727→const PIECES = {
   728→  'K': '♔', 'Q': '♕', 'R': '♖', 'B': '♗', 'N': '♘', 'P': '♙',
   729→  'k': '♚', 'q': '♛', 'r': '♜', 'b': '♝', 'n': '♞', 'p': '♟'
   730→};
   731→
   732→function renderChessBoard(fen, bestMove) {
   733→  if (!fen) {
   734→    chessBoard.innerHTML = '<div class="placeholder" style="grid-column: span 8; grid-row: span 8;">No position to display</div>';
   735→    return;
   736→  }
   737→  
   738→  // Parse FEN - get just the board part
   739→  const boardPart = fen.split(' ')[0];
   740→  const ranks = boardPart.split('/');
   741→  
   742→  // Parse best move to get from/to squares
   743→  let fromSquare = null;
   744→  let toSquare = null;
   745→  
   746→  if (bestMove) {
   747→    // Try direct from/to first
   748→    if (bestMove.from && bestMove.to) {
   749→      fromSquare = bestMove.from;
   750→      toSquare = bestMove.to;
   751→    } 
   752→    // Then try parsing from move string
   753→    else if (bestMove.move) {
   754→      const move = bestMove.move;
   755→      if (move.length >= 4 && /^[a-h][1-8][a-h][1-8]/.test(move)) {
   756→        fromSquare = move.substring(0, 2);
   757→        toSquare = move.substring(2, 4);
   758→      }
   759→    }
   760→  }
   761→  
   762→  console.log('[Panel] Rendering board, from:', fromSquare, 'to:', toSquare, 'flipped:', boardFlipped);
   763→  
   764→  // Generate 64 squares - handle flipping
   765→  let html = '';
   766→  
   767→  for (let visualRow = 0; visualRow < 8; visualRow++) {
   768→    for (let visualCol = 0; visualCol < 8; visualCol++) {
   769→      // Map visual position to actual board position
   770→      const actualRow = boardFlipped ? (7 - visualRow) : visualRow;
   771→      const actualCol = boardFlipped ? (7 - visualCol) : visualCol;
   772→      
   773→      const rankNum = 8 - actualRow; // 8 to 1
   774→      const file = String.fromCharCode(97 + actualCol); // a-h
   775→      const square = file + rankNum;
   776→      
   777→      // Get piece at this position
   778→      const rank = ranks[actualRow];
   779→      const piece = getPieceAtPosition(rank, actualCol);
   780→      
   781→      // Determine square color (based on actual position, not visual)
   782→      const isLight = (actualRow + actualCol) % 2 === 0;
   783→      const highlight = getSquareHighlight(square, fromSquare, toSquare);
   784→      
   785→      if (piece) {
   786→        const isWhite = piece === piece.toUpperCase();
   787→        const pieceChar = PIECES[piece] || piece;
   788→        html += `<div class="chess-square ${isLight ? 'light' : 'dark'} ${highlight}" data-square="${square}">
   789→          <span class="piece ${isWhite ? 'white' : 'black'}">${pieceChar}</span>
   790→        </div>`;
   791→      } else {
   792→        html += `<div class="chess-square ${isLight ? 'light' : 'dark'} ${highlight}" data-square="${square}"></div>`;
   793→      }
   794→    }
   795→  }
   796→  
   797→  chessBoard.innerHTML = html;
   798→}
   799→
   800→// Get piece at a specific column in a FEN rank string
   801→function getPieceAtPosition(rankStr, col) {
   802→  let currentCol = 0;
   803→  for (const char of rankStr) {
   804→    if ('12345678'.includes(char)) {
   805→      currentCol += parseInt(char);
   806→    } else {
   807→      if (currentCol === col) {
   808→        return char;
   809→      }
