          max_tokens: 10,
          messages: [{ role: 'user', content: 'Say "OK" and nothing else.' }]
        })
      });

      if (!response.ok) {
        const err = await response.json().catch(() => ({}));
        console.error('[Chess Study] OpenRouter test failed:', err);
