// Display comparison results from multi-model analysis
function displayComparison(comparison) {
  comparisonBox.style.display = 'block';

  // Build results HTML
  let html = '';

  // Show agreements
  if (comparison.agreements && comparison.agreements.length > 0) {
    comparison.agreements.forEach(provider => {
      const providerName = getProviderDisplayName(provider);
      html += `<div class="comparison-item agree"><span class="comparison-icon">✓</span> ${providerName}</div>`;
    });
  }

  // Show disagreements
  if (comparison.disagreements && comparison.disagreements.length > 0) {
    comparison.disagreements.forEach(d => {
      const providerName = getProviderDisplayName(d.provider);
      html += `<div class="comparison-item disagree"><span class="comparison-icon">⚠</span> ${providerName} disagrees on ${d.square}: sees ${d.otherPiece} instead of ${d.defaultPiece}</div>`;
    });
  }

  comparisonResults.innerHTML = html;

  // Show which model is being used
  const defaultName = getProviderDisplayName(comparison.defaultProvider);
  comparisonDefault.innerHTML = `Using: ${defaultName} (default) <span class="default-star">★</span>`;
}

